<template>
  <div>
    <pre id="egp-json-container"></pre>
    <span hidden>{{jsonValue}}</span>
  </div>
</template>

<script>
export default {
  name: 'json-container',
  data () {
    return {};
  },
  props: ['value', 'scrollIntoView'],
  computed: {
    jsonValue: function() {
      const el = document.getElementById('egp-json-container');
      if (el) {
        el.innerHTML = this.syntaxHighlight(this.value);
        el.scrollIntoView(this.scrollIntoView);
      }
      return this.syntaxHighlight(this.value);
    }
  },
  methods: {
    syntaxHighlight(json) {
      if (typeof json != 'string') {
        json = JSON.stringify(json, undefined, 2);
      }
      json = json.replace(/&/g, '&').replace(/</g, '<').replace(/>/g, '>');
      return json.replace(/("(\\u[a-zA-Z0-9]{4}|\\[^u]|[^\\"])*"(\s*:)?|\b(true|false|null)\b|-?\d+(?:\.\d*)?(?:[eE][+\-]?\d+)?)/g, function(match) {
        var cls = 'number';
        if (/^"/.test(match)) {
          if (/:$/.test(match)) {
            cls = 'key';
          } else {
            cls = 'string';
          }
        } else if (/true|false/.test(match)) {
          cls = 'boolean';
        } else if (/null/.test(match)) {
          cls = 'null';
        }
        return '<span class="' + cls + '">' + match + '</span>';
      });
    }
  }
}
</script>

<style>
pre {padding: 5px; margin: 5px; font-size: 1.05em; }
.string { color: green; }
.number { color: darkorange; }
.boolean { color: blue; }
.null { color: magenta; }
.key { color: red; }
</style>
